package ticket.booking;

import java.io.IOException;
import java.util.ArrayList;
import java.util.Scanner;

import ticket.booking.entities.User;
import ticket.booking.services.UserBookingService;
import ticket.booking.utils.UserServiceUtils;


public class App
{
    public static void main(String[] args)
    {
        System.out.println("Command Line IRCTC");
        Scanner sc = new Scanner(System.in);
        int option = 0;
        UserBookingService userBookingService;
        try
        {
            userBookingService = new UserBookingService();
        }
        catch (IOException e)
        {
           System.out.println("Error: User booking service is not available");
           return;
        }
        while(option!=7)
        {
            System.out.println("Choose Option");
            System.out.println("1. Sign Up");
            System.out.println("2. Sign In");
            System.out.println("3. Fetch Booking Details");
            System.out.println("4. Search Trains");
            System.out.println("5. Book a Seat");
            System.out.println("6. Cancel Booking");
            System.out.println("7. Sign Out & Exit");

            option = sc.nextInt();
            switch(option)
            {
                case 1:
                    sc.nextLine(); // consume the newline left by nextInt()
                    System.out.println("Enter Your Username");
                    String usernameToSignUp = sc.nextLine();
                    System.out.println("Enter Your Name");
                    String nameToSignUp = sc.nextLine();
                    System.out.println("Enter Your Password");
                    String passwordToSignUp = sc.nextLine();
                    System.out.println("Enter Your Email Address");
                    String emailToSignUp = sc.nextLine();
                    System.out.println("Enter Your Phone Number");
                    String phoneToSignUp = sc.nextLine();
                    User user = new User(
                            nameToSignUp,
                            usernameToSignUp,
                            passwordToSignUp,
                            UserServiceUtils.hashPassword(passwordToSignUp),
                            emailToSignUp,
                            phoneToSignUp,
                            new ArrayList<>()                   // empty list of tickets
                    );
                    System.out.println("Your Details are as follows : "+user);
                    userBookingService.signUpUser(user);
                    break;
                case 2:
                    sc.nextLine(); // consume the newline left by nextInt()
                    System.out.println("Login Method");
                    System.out.println("1. Via Phone Number");
                    System.out.println("2. Via Email Address");
                    System.out.println("3. Via Username");
                    String loginMethod = sc.nextLine();
                    String loginIdentifier = "";
                    
                    if(loginMethod.equals("1"))
                    {
                        System.out.println("Enter Phone Number");
                        loginIdentifier = sc.nextLine();
                    }
                    else if(loginMethod.equals("2"))
                    {
                        System.out.println("Enter Email Address");
                        loginIdentifier = sc.nextLine();
                    }
                    else if(loginMethod.equals("3"))
                    {
                        System.out.println("Enter Username");
                        loginIdentifier = sc.nextLine();
                    }
                    else
                    {
                        System.out.println("Wrong Login Method");
                        break;
                    }
                    
                    System.out.println("Enter Your Password");
                    String passwordToSignIn = sc.nextLine();
                    
                    try {
                        Boolean loginSuccess = userBookingService.loginUser(loginMethod, loginIdentifier, passwordToSignIn);
                        if(loginSuccess)
                        {
                            System.out.println("Login Successful!");

                        }
                        else
                        {
                            System.out.println("Login Failed! Invalid credentials.");
                        }
                    } catch (Exception e) {
                        System.out.println("Error during login: " + e.getMessage());
                    }
                    break;
                case 3:
                    if(userBookingService.getCurrentUser() == null)
                    {
                        System.out.println("Please sign in first.");
                        break;
                    }
                    userBookingService.fetchBooking();
                    break;
                case 4:
                    System.out.println("Search Trains - Feature not yet implemented");
                    break;
                case 5:
                    System.out.println("Book a Seat - Feature not yet implemented");
                    break;
                case 6:
                    if(userBookingService.getCurrentUser() == null)
                    {
                        System.out.println("Please sign in first.");
                        break;
                    }
                    sc.nextLine(); // consume the newline left by nextInt()
                    System.out.println("Enter Ticket ID to cancel");
                    String ticketIdToCancel = sc.nextLine();
                    userBookingService.cancelBooking(ticketIdToCancel);
                    break;
            }
        }
    }
}
